CREATE TABLE shipment_data (
    shipment_id INT PRIMARY KEY,
    weight DECIMAL,
    volume DECIMAL
);

CREATE TABLE load_planning (
    load_id INT PRIMARY KEY,
    shipment_id INT, 
    load_capacity DECIMAL
);

CREATE TABLE shipment_details (
    details_id INT PRIMARY KEY,
    shipment_id INT,
    weight DECIMAL,
    volume DECIMAL
);

CREATE TABLE Shipments (
    shipment_id INT PRIMARY KEY,
    weight DECIMAL,
    volume DECIMAL
);

CREATE TABLE LoadPlans (
    loadplan_id INT PRIMARY KEY,
    shipment_id INT,
    FOREIGN KEY (shipment_id) REFERENCES Shipments(shipment_id)
);

CREATE TABLE WeightData (
    weight_id INT PRIMARY KEY,
    shipment_id INT,
    weight DECIMAL,
    FOREIGN KEY (shipment_id) REFERENCES Shipments(shipment_id)
);

CREATE TABLE VolumeData (
    volume_id INT PRIMARY KEY,
    shipment_id INT,
    volume DECIMAL,
    FOREIGN KEY (shipment_id) REFERENCES Shipments(shipment_id)
);

CREATE TABLE shipments (
    shipment_id UUID PRIMARY KEY,
    weight DECIMAL,
    volume DECIMAL
);

CREATE TABLE load_plans (
    plan_id UUID PRIMARY KEY,
    shipment_id UUID,
    load_date TIMESTAMP,
    completed BOOLEAN
);

CREATE TABLE trucks (
    truck_id UUID PRIMARY KEY,
    weight_capacity DECIMAL,
    volume_capacity DECIMAL
);

CREATE TABLE routes (
    route_id UUID PRIMARY KEY,
    start_location TEXT,
    end_location TEXT
);

CREATE TABLE assignments (
    assignment_id UUID PRIMARY KEY,
    plan_id UUID,
    truck_id UUID,
    route_id UUID
);
