tables.table_schema:
CREATE TABLE shipment_logs (
  log_id UUID PRIMARY KEY,
  shipment_id UUID,
  log_timestamp TIMESTAMP,
  log_description TEXT
);

CREATE TABLE shipment_records (
  shipment_id INT PRIMARY KEY,
  shipment_date DATE,
  arrival_date DATE,
  status VARCHAR(50)
);

CREATE TABLE shipment_delays (
  delay_id INT PRIMARY KEY,
  shipment_id INT,
  delay_reason VARCHAR(255),
  delay_duration INT
);

CREATE TABLE shipment_feedback (
  feedback_id INT PRIMARY KEY,
  shipment_id INT,
  customer_name VARCHAR(100),
  feedback_comment TEXT
);

CREATE TABLE shipment_status (
    shipment_id INT PRIMARY KEY,
    status VARCHAR(50),
    estimated_delivery_date DATE,
    actual_delivery_date DATE
);

CREATE TABLE shipment_log (
    log_id INT PRIMARY KEY,
    shipment_id INT,
    log_description TEXT,
    log_timestamp TIMESTAMP
);

CREATE TABLE shipment_delay (
    delay_id INT PRIMARY KEY,
    shipment_id INT,
    reason VARCHAR(200),
    delay_duration INTERVAL
);

CREATE TABLE shipments (
    shipment_id INT PRIMARY KEY,
    sender_name VARCHAR(255),
    receiver_name VARCHAR(255),
    shipment_date DATE
);

CREATE TABLE delays (
    delay_id INT PRIMARY KEY,
    shipment_id INT REFERENCES shipments(shipment_id),
    reason TEXT,
    delay_duration INTERVAL
);

CREATE TABLE notifications (
    notification_id INT PRIMARY KEY,
    shipment_id INT REFERENCES shipments(shipment_id),
    message TEXT,
    notification_date DATE
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100)
);
