CREATE TABLE TransactionsHistory (
    transaction_id INT,
    user_id INT,
    product_id INT,
    timestamp TIMESTAMP,
    FOREIGN KEY (transaction_id) REFERENCES Transactions(transaction_id),
    FOREIGN KEY (user_id) REFERENCES Users(user_id),
    FOREIGN KEY (product_id) REFERENCES Products(product_id)
);

