tables.table_schema:
CREATE TABLE Inventory ( 
    product_id INT PRIMARY KEY, 
    product_name VARCHAR(255) NOT NULL, 
    quantity INT CHECK (quantity >= 0) 
);

CREATE TABLE Orders ( 
    order_id INT PRIMARY KEY, 
    product_id INT REFERENCES Inventory(product_id), 
    order_date DATE, 
    order_quantity INT CHECK (order_quantity > 0) 
);

CREATE TABLE inventory (
    product_id INT PRIMARY KEY,
    quantity INT NOT NULL,
    turnover_rate FLOAT CHECK (turnover_rate >= 0),
    CONSTRAINT turnover_policy CHECK (quantity * turnover_rate <= 1000)
);

CREATE TABLE turnover_policies (
    policy_id INT PRIMARY KEY,
    policy_name VARCHAR(50) UNIQUE,
    max_quantity INT NOT NULL,
    max_turnover_rate FLOAT NOT NULL
);

CREATE TABLE products (
  product_id INT PRIMARY KEY,
  product_name STRING,
  quantity INT,
  price DECIMAL
);

CREATE TABLE customers (
  customer_id INT PRIMARY KEY,
  customer_name STRING,
  address STRING
);

CREATE TABLE inventory (
  product_id INT PRIMARY KEY,
  quantity_in_stock INT,
  reorder_level INT,
  FOREIGN KEY (product_id) REFERENCES products(product_id)
);

CREATE TABLE Suppliers (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255) NOT NULL
);

CREATE TABLE Inventory_Log (
    log_id INT PRIMARY KEY,
    item_id INT REFERENCES Inventory_Items(item_id),
    log_date TIMESTAMP,
    log_description TEXT
);
