tables.table_schema:
CREATE TABLE landmark_court_decisions_womens_rights (
    decision_id INT PRIMARY KEY,
    case_name VARCHAR(255),
    decision_date DATE,
    court_name VARCHAR(100),
    landmark_decision BOOLEAN
);

CREATE TABLE judges (
    judge_id INT PRIMARY KEY,
    judge_name VARCHAR(100),
    court_name VARCHAR(100),
    years_on_bench INT
);

CREATE TABLE parties (
    party_id INT PRIMARY KEY,
    party_name VARCHAR(100),
    party_type VARCHAR(50)
);

CREATE TABLE legal_counsel (
    counsel_id INT PRIMARY KEY,
    counsel_name VARCHAR(100),
    law_firm VARCHAR(100),
    specialization VARCHAR(50)
);
