tables.table_schema:
CREATE TABLE legal_reforms (
    reform_id UUID PRIMARY KEY,
    reform_name VARCHAR(100),
    implementation_date DATE,
    jurisdiction VARCHAR(50)
);

CREATE TABLE challenges (
    challenge_id UUID PRIMARY KEY,
    challenge_desc TEXT,
    reform_id UUID REFERENCES legal_reforms(reform_id)
);

CREATE TABLE women_rights_reforms (
    reform_id INT PRIMARY KEY,
    reform_title VARCHAR(255),
    enacted_date DATE,
    implementation_status VARCHAR(50)
);

CREATE TABLE reform_progress (
    progress_id INT PRIMARY KEY,
    reform_id INT,
    progress_description TEXT,
    progress_date DATE,
    CONSTRAINT fk_reform_id FOREIGN KEY (reform_id) REFERENCES women_rights_reforms(reform_id)
);

CREATE TABLE reform_challenges (
    challenge_id INT PRIMARY KEY,
    reform_id INT,
    challenge_description TEXT,
    challenge_date DATE,
    CONSTRAINT fk_reform_id FOREIGN KEY (reform_id) REFERENCES women_rights_reforms(reform_id)
);

CREATE TABLE legal_reforms (
  id INT PRIMARY KEY,
  reform_name TEXT NOT NULL,
  implementation_date DATE,
  progress_status TEXT
);

CREATE TABLE challenges (
  id INT PRIMARY KEY,
  challenge_name TEXT NOT NULL,
  description TEXT,
  resolution_status TEXT
);

CREATE TABLE progress_tracking (
  id INT PRIMARY KEY,
  reform_id INT NOT NULL,
  progress_date DATE,
  progress_update TEXT
);

CREATE TABLE stakeholders (
  id INT PRIMARY KEY,
  stakeholder_name TEXT NOT NULL,
  involvement_level TEXT,
  contact_information TEXT
);

CREATE TABLE reform_status (
    reform_id INT PRIMARY KEY,
    reform_name TEXT NOT NULL,
    implementation_status TEXT,
    challenges TEXT
);

CREATE TABLE reform_details (
    reform_id INT PRIMARY KEY,
    reform_description TEXT,
    implementation_start_date DATE,
    implementation_end_date DATE
);

CREATE TABLE progress_updates (
    update_id INT PRIMARY KEY,
    reform_id INT REFERENCES reform_status(reform_id),
    update_date DATE,
    update_description TEXT
);

CREATE TABLE reform_impact (
    impact_id INT PRIMARY KEY,
    reform_id INT REFERENCES reform_status(reform_id),
    impact_type TEXT,
    impact_description TEXT
);

CREATE TABLE reform_challenges (
    challenge_id INT PRIMARY KEY,
    reform_id INT REFERENCES reform_status(reform_id),
    challenge_description TEXT,
    proposed_solutions TEXT
);
