tables.table_schema:
CREATE TABLE GenderPayGapData (
  industry VARCHAR(50),
  region VARCHAR(50),
  gender VARCHAR(10),
  avg_salary DECIMAL(10, 2)
);

CREATE TABLE gender_pay_gap_by_industry (
    industry_id INT PRIMARY KEY,
    industry_name VARCHAR,
    average_male_salary DECIMAL,
    average_female_salary DECIMAL
);

CREATE TABLE industry_pay_gap_data (
    industry_id INT PRIMARY KEY,
    industry_name VARCHAR(255),
    avg_pay_gap DECIMAL(10,2)
);

CREATE TABLE regional_pay_gap_data (
    region_id INT PRIMARY KEY,
    region_name VARCHAR(255),
    avg_pay_gap DECIMAL(10,2)
);

CREATE TABLE gender_pay_gap_by_industry (
	industry_id INT,
	industry_name VARCHAR(255),
	gender VARCHAR(10),
	pay_gap DECIMAL(10,2)
);

CREATE TABLE gender_pay_gap_by_region (
	region_id INT,
	region_name VARCHAR(255),
	gender VARCHAR(10),
	pay_gap DECIMAL(10,2)
);

CREATE TABLE industry_data (
	industry_id INT,
	industry_name VARCHAR(255),
	number_of_employees INT,
	average_salary DECIMAL(10,2)
);

CREATE TABLE industry_data (
    id INT PRIMARY KEY,
    industry_name VARCHAR(255) UNIQUE,
    industry_code INT
);

CREATE TABLE region_data (
    id INT PRIMARY KEY,
    region_name VARCHAR(255) UNIQUE,
    region_code INT
);
