tables.table_schema:
CREATE TABLE Conventions (
    convention_id INT PRIMARY KEY,
    convention_name VARCHAR(255),
    ratification_date DATE,
    country VARCHAR(50)
);

CREATE TABLE Treaty_Status (
    treaty_id SERIAL PRIMARY KEY,
    convention_id INT REFERENCES International_Conventions(convention_id),
    ratification_status BOOLEAN,
    notes TEXT
);

CREATE TABLE Treaty_Detail (
    detail_id SERIAL PRIMARY KEY,
    treaty_id INT REFERENCES Treaty_Status(treaty_id),
    article_number INT NOT NULL,
    article_text TEXT
);

CREATE TABLE `countries` (
  `id` INT PRIMARY KEY,
  `name` VARCHAR(50) NOT NULL
);

CREATE TABLE `articles` (
  `id` INT PRIMARY KEY,
  `title` VARCHAR(150) NOT NULL,
  `content` TEXT
);
