CREATE TABLE statutes (
    statute_id UUID PRIMARY KEY,
    statute_name VARCHAR(255),
    jurisdiction VARCHAR(255),
    enactment_date DATE
);

CREATE TABLE contracts (
    contract_id UUID PRIMARY KEY,
    contract_name TEXT,
    contract_date DATE,
    contract_text TEXT
);

CREATE TABLE agreements (
    agreement_id UUID PRIMARY KEY,
    agreement_name TEXT,
