tables.table_schema:
CREATE TABLE environmental_permit_applications (
  application_id UUID PRIMARY KEY,
  applicant_name TEXT,
  permit_type TEXT,
  application_date DATE
);

CREATE TABLE permit_conditions (
    condition_id INT PRIMARY KEY,
    approval_id INT REFERENCES permit_approval(approval_id),
    condition_description TEXT
);

CREATE TABLE permit_applications (
   permit_id INT PRIMARY KEY,
   applicant_name VARCHAR(255),
   application_date DATE,
   application_status VARCHAR(50)
);

CREATE TABLE permit_approvals (
   approval_id INT PRIMARY KEY,
   permit_id INT,
   approval_date DATE,
   approval_status VARCHAR(50)
);

CREATE TABLE permit_documents (
   document_id INT PRIMARY KEY,
   permit_id INT,
   document_type VARCHAR(50),
   document_content BYTEA
);

CREATE TABLE permit_locations (
   location_id INT PRIMARY KEY,
   permit_id INT,
   location_name VARCHAR(100),
   location_address VARCHAR(255)
);

CREATE TABLE permit_applications (
    application_id INT PRIMARY KEY,
    applicant_name VARCHAR(50),
    application_date DATE,
    permit_type VARCHAR(50)
);

CREATE TABLE permit_details (
    detail_id INT PRIMARY KEY,
    application_id INT,
    detail_description TEXT,
    CONSTRAINT fk_application_id FOREIGN KEY (application_id) REFERENCES permit_applications(application_id)
);

CREATE TABLE permit_locations (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(50),
    region VARCHAR(50)
);
