tables.table_schema:
CREATE TABLE EmissionsData (
    plant_id INT,
    emission_type VARCHAR(255),
    emission_value FLOAT,
    emission_date DATE
);

CREATE TABLE Plants (
    plant_id INT,
    plant_name VARCHAR(255),
    plant_location VARCHAR(255),
    plant_owner VARCHAR(255)
);

CREATE TABLE emission_trends (
    trend_id UUID PRIMARY KEY,
    plant_id UUID REFERENCES industrial_plants(plant_id),
    trend_date DATE,
    trend_value FLOAT
);

CREATE TABLE plant_statistics (
    stat_id UUID PRIMARY KEY,
    plant_id UUID REFERENCES industrial_plants(plant_id),
    year INT,
    emission_total FLOAT
);

CREATE TABLE emissions_data (
    plant_id INT,
    emission_type VARCHAR(50),
    emission_value FLOAT,
    measured_date DATE
);

CREATE TABLE plants (
    plant_id INT,
    plant_name VARCHAR(100),
    plant_location VARCHAR(100),
    industry_type VARCHAR(50)
);

CREATE TABLE data_sensors (
    sensor_id INT,
    sensor_type VARCHAR(50),
    calibration_date DATE,
    plant_id INT
);

CREATE TABLE plant_owners (
    owner_id INT,
    owner_name VARCHAR(100),
    contact_email VARCHAR(100)
);
