tables.table_schema:
CREATE TABLE wildlife_protection_areas (
    area_id INT PRIMARY KEY,
    area_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL
);

CREATE TABLE wildlife_areas (
    area_id INT PRIMARY KEY,
    area_name VARCHAR(255),
    location VARCHAR(255)
);

CREATE TABLE wildlife_rangers (
    ranger_id INT PRIMARY KEY,
    area_id INT,
    ranger_name VARCHAR(255),
    contact_number VARCHAR(20)
);

CREATE TABLE wildlife_areas (
  area_id INT PRIMARY KEY,
  area_name VARCHAR(255),
  location VARCHAR(255)
);

CREATE TABLE animal_species (
  species_id INT PRIMARY KEY,
  species_name VARCHAR(255),
  endangered BOOLEAN
);

CREATE TABLE conservationists (
  conservationist_id INT PRIMARY KEY,
  name VARCHAR(255),
  specialization VARCHAR(255)
);

CREATE TABLE sightings (
  sighting_id INT PRIMARY KEY,
  species_id INT,
  area_id INT,
  sighting_date DATE
);
