tables.table_schema:
CREATE TABLE MeasurementSources (
    source_id INT PRIMARY KEY,
    source_name VARCHAR(100),
    source_type VARCHAR(50),
    source_location VARCHAR(100)
);

CREATE TABLE measurement_sources (
   source_id INT PRIMARY KEY,
   source_name VARCHAR(50),
   location VARCHAR(100)
);

CREATE TABLE measurement_errors (
   error_id INT PRIMARY KEY,
   measurement_id INT,
   error_type VARCHAR(50),
   error_description TEXT
);

CREATE TABLE WaterSources (
    source_id UUID PRIMARY KEY,
    source_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL
);

CREATE TABLE Parameters (
    parameter_id UUID PRIMARY KEY,
    parameter_name VARCHAR(255) NOT NULL
);

CREATE TABLE MeasurementParameters (
    measurement_id UUID REFERENCES Measurements(measurement_id),
    parameter_id UUID REFERENCES Parameters(parameter_id),
    value FLOAT
);

CREATE TABLE WaterQualityMeasurements (
    measurement_id INT PRIMARY KEY,
    source_id INT,
    measurement_time TIMESTAMP,
    ph_level DECIMAL,
    turbidity DECIMAL
);

CREATE TABLE MeasurementSources (
    source_id INT PRIMARY KEY,
    source_name VARCHAR(50),
    location VARCHAR(100),
    contact_person VARCHAR(50)
);

CREATE TABLE WaterQualityParameters (
    parameter_id INT PRIMARY KEY,
    parameter_name VARCHAR(50),
    unit VARCHAR(20)
);

CREATE TABLE MeasurementTags (
    tag_id INT PRIMARY KEY,
    tag_name VARCHAR(50),
    description VARCHAR(100)
);

CREATE TABLE MeasurementResults (
    result_id INT PRIMARY KEY,
    measurement_id INT,
    parameter_id INT,
    result_value DECIMAL,
    result_date DATE
);
