tables.table_schema:
CREATE TABLE AirQualityMonitoringStation (
    station_id UUID PRIMARY KEY,
    station_name VARCHAR(255),
    location_info JSONB
);

CREATE TABLE EmissionSources (
    source_id UUID PRIMARY KEY,
    source_name VARCHAR(255),
    location JSONB,
    emission_type VARCHAR(100)
);

CREATE TABLE EmissionLimits (
    id INT PRIMARY KEY,
    facility VARCHAR(255),
    pollutant VARCHAR(255),
    limit DECIMAL
);

CREATE TABLE MonitoringStations (
    id INT PRIMARY KEY,
    location VARCHAR(255),
    capacity INT,
    technician VARCHAR(255)
);

CREATE TABLE air_quality_compliance (
    monitoring_station_id INT PRIMARY KEY,
    pollutant_name TEXT,
    pollutant_level FLOAT,
    emission_limit FLOAT
);

CREATE TABLE monitoring_stations (
    station_id INT PRIMARY KEY,
    station_name TEXT,
    location TEXT,
    manager_name TEXT
);

CREATE TABLE pollutants (
    pollutant_id INT PRIMARY KEY,
    pollutant_name TEXT,
    pollutant_description TEXT
);

CREATE TABLE emission_limits (
    pollutant_id INT,
    max_limit FLOAT,
    min_limit FLOAT,
    PRIMARY KEY (pollutant_id)
);
