tables.table_schema:
CREATE TABLE WasteManagementPractices (
  id INT PRIMARY KEY,
  practice_name VARCHAR(255),
  description TEXT
);

CREATE TABLE DisposalMethods (
  id INT PRIMARY KEY,
  method_name VARCHAR(255),
  description TEXT
);

CREATE TABLE waste_management_practices (
  id SERIAL PRIMARY KEY,
  practice_name VARCHAR(100) NOT NULL,
  description TEXT,
  location VARCHAR(200) NOT NULL
);

CREATE TABLE disposal_methods (
  id SERIAL PRIMARY KEY,
  method_name VARCHAR(100) NOT NULL,
  description TEXT,
  disposal_site VARCHAR(200) NOT NULL
);
