tables.table_schema:
CREATE TABLE impact_categories (
  category_id INT PRIMARY KEY,
  category_name VARCHAR(100),
  category_description TEXT
);

CREATE TABLE environmental_impact_assessment (
	emp_project_id SERIAL PRIMARY KEY,
	project_name TEXT,
	assessment_date DATE,
	impact_category TEXT
);

CREATE TABLE project_investors (
	investor_id SERIAL PRIMARY KEY,
	investor_name TEXT,
	investor_type TEXT,
	investment_amount DECIMAL
);

CREATE TABLE project_timeline (
	timeline_id SERIAL PRIMARY KEY,
	project_id INT REFERENCES environmental_impact_assessment(emp_project_id),
	start_date DATE,
	completion_date DATE
);

CREATE TABLE stakeholders (
  stakeholder_id UUID PRIMARY KEY,
  stakeholder_name VARCHAR(255) NOT NULL,
  organization VARCHAR(255),
  type VARCHAR(50)
);
