tables.table_schema:
CREATE TABLE legal_cases (
  case_id UUID PRIMARY KEY,
  case_name VARCHAR(255),
  court_name VARCHAR(255),
  case_date DATE
);

CREATE TABLE citations (
  citation_id UUID PRIMARY KEY,
  cited_case_id UUID,
  citing_case_id UUID,
  citation_date DATE
);

CREATE TABLE judges (
  judge_id UUID PRIMARY KEY,
  judge_name VARCHAR(255),
  court_name VARCHAR(255)
);

CREATE TABLE lawyers (
 lawyer_id UUID PRIMARY KEY,
 lawyer_name TEXT NOT NULL,
 lawyer_email TEXT NOT NULL,
 law_firm TEXT NOT NULL
);

CREATE TABLE law_firms (
 firm_id UUID PRIMARY KEY,
 firm_name TEXT NOT NULL,
 location TEXT NOT NULL
);

CREATE TABLE case_references (
 reference_id UUID PRIMARY KEY,
 referencing_case_id UUID NOT NULL,
 referenced_case_id UUID NOT NULL,
 reference_date DATE NOT NULL
);

CREATE TABLE CaseReferences (
  reference_id INT PRIMARY KEY,
  case_id INT,
  referenced_case_id INT
);

CREATE TABLE Courts (
  court_id INT PRIMARY KEY,
  court_name VARCHAR(255),
  court_location VARCHAR(255)
);

CREATE TABLE Judges (
  judge_id INT PRIMARY KEY,
  judge_name VARCHAR(255),
  years_experience INT
);

CREATE TABLE Citations (
  citation_id INT PRIMARY KEY,
  case_id INT,
  cited_by_case_id INT
);
