CREATE TABLE patents (
   id SERIAL PRIMARY KEY,
   patent_number VARCHAR(255) NOT NULL,
   filing_date DATE NOT NULL,
   inventor VARCHAR(255) NOT NULL
);

CREATE TABLE inventors (
   id SERIAL PRIMARY KEY,
   first_name VARCHAR(255) NOT NULL,
   last_name VARCHAR(255) NOT NULL
);

CREATE TABLE patent_files (
   id SERIAL PRIMARY KEY,
   file_name VARCHAR(255) NOT NULL,
   file_type VARCHAR(50) NOT NULL,
   file_size INT
);

CREATE TABLE patent_owners (
   id SERIAL PRIMARY KEY,
   owner_name VARCHAR(255) NOT NULL,
   country VARCHAR(50) NOT NULL
);

CREATE TABLE patents (
  patent_id UUID PRIMARY KEY,
  filing_date DATE NOT NULL,
  inventor_id UUID
);

CREATE TABLE inventors (
  inventor_id UUID PRIMARY KEY,
  full_name TEXT NOT NULL,
  birth_date DATE
);

CREATE TABLE companies (
  company_id UUID PRIMARY KEY,
  company_name TEXT NOT NULL,
  headquarters_location TEXT
);

CREATE TABLE patent_inventors (
  patent_id UUID,
  inventor_id UUID,
  PRIMARY KEY(patent_id, inventor_id),
  FOREIGN KEY(patent_id) REFERENCES patents(patent_id),
  FOREIGN KEY(inventor_id) REFERENCES inventors(inventor_id)
);

CREATE TABLE company_patents (
  company_id UUID,
  patent_id UUID,
  PRIMARY KEY(company_id, patent_id),
  FOREIGN KEY(company_id) REFERENCES companies(company_id),
  FOREIGN KEY(patent_id) REFERENCES patents(patent_id)
);
