tables.table_schema:
CREATE TABLE intellectual_property_rights (
    id INT PRIMARY KEY,
    property_name VARCHAR(255),
    expiration_date DATE,
    renewal_status BOOLEAN
);

CREATE TABLE IntellectualProperty (
    id INT PRIMARY KEY,
    name STRING NOT NULL,
    expiration_date DATE,
    renewal_status BOOL
);

CREATE TABLE Reminder (
    id INT PRIMARY KEY,
    reminder_date DATE,
    intellectual_property_id INT,
    FOREIGN KEY (intellectual_property_id) REFERENCES IntellectualProperty(id)
);

CREATE TABLE NotificationLog (
    id INT PRIMARY KEY,
    notification_date DATE,
    reminder_id INT,
    FOREIGN KEY (reminder_id) REFERENCES Reminder(id)
);

CREATE TABLE IntellectualPropertyRights (
    id INT PRIMARY KEY,
    property_name VARCHAR(255),
    renewal_due_date DATE,
    reminder_sent BOOLEAN
);

CREATE TABLE ExpiringRightsReminders (
    id INT PRIMARY KEY,
    intellectual_property_id INT,
    reminder_date DATE,
    reminder_message TEXT
);

CREATE TABLE Users (
    id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(255)
);

CREATE TABLE AuditTrail (
    id INT PRIMARY KEY,
    event_type VARCHAR(50),
    event_timestamp TIMESTAMP,
    user_id INT
);

CREATE TABLE RightsRenewalReminders (
    reminder_id UUID PRIMARY KEY,
    ipr_id UUID REFERENCES IntellectualPropertyRights(ipr_id),
    reminder_date DATE NOT NULL,
    reminder_message TEXT
);

CREATE TABLE Users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(255) NOT NULL,
    phone_number VARCHAR(20)
);

CREATE TABLE UserNotifications (
    notification_id UUID PRIMARY KEY,
    user_id UUID REFERENCES Users(user_id),
    notification_message TEXT NOT NULL,
    notification_date TIMESTAMP NOT NULL
);

CREATE TABLE RightsRenewalHistory (
    history_id UUID PRIMARY KEY,
    ipr_id UUID REFERENCES IntellectualPropertyRights(ipr_id),
    renewal_date DATE NOT NULL,
    renewed_by UUID REFERENCES Users(user_id)
);
