tables.table_schema:
CREATE TABLE intellectual_property_documents (
    id INT PRIMARY KEY,
    document_name VARCHAR(255),
    document_type VARCHAR(50),
    document_content BYTEA
);

CREATE TABLE patent_drawings (
    id SERIAL PRIMARY KEY,
    image_url VARCHAR(255),
    title VARCHAR(255),
    description TEXT
);

CREATE TABLE copyrights (
    id INT PRIMARY KEY,
    certificate_number VARCHAR(255),
    certificate_name VARCHAR(255),
    registration_date DATE
);

CREATE TABLE inventors (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    country VARCHAR(50)
);

CREATE TABLE patent_owners (
    id INT PRIMARY KEY,
    patent_id INT,
    owner_id INT
);

CREATE TABLE document_types (
    id INT PRIMARY KEY,
    type VARCHAR(50)
);
