tables.table_schema:
CREATE TABLE patents (
    patent_id INT PRIMARY KEY,
    patent_title VARCHAR(255),
    filing_date DATE,
    inventor VARCHAR(255)
);

CREATE TABLE trademarks (
    trademark_id INT PRIMARY KEY,
    trademark_name VARCHAR(255),
    registration_date DATE,
    trademark_owner VARCHAR(255)
);

CREATE TABLE copyrights (
    copyright_id INT PRIMARY KEY,
    work_title VARCHAR(255),
    registration_date DATE,
    author VARCHAR(255)
);

CREATE TABLE IP_owners (
    owner_id INT PRIMARY KEY,
    owner_name VARCHAR(255),
    location VARCHAR(255)
);

CREATE TABLE IP_analytics (
    analysis_id INT PRIMARY KEY,
    IP_id INT,
    analysis_date DATE,
    insights TEXT
);
