tables.table_schema:
CREATE TABLE judges (
  id INT PRIMARY KEY,
  judge_name VARCHAR(255),
  court VARCHAR(255),
  years_on_bench INT
);

CREATE TABLE legal_topics (
    topic_id UUID PRIMARY KEY,
    topic_name VARCHAR(255),
    related_cases JSONB
);

CREATE TABLE lawyers (
  lawyer_id INT PRIMARY KEY,
  lawyer_name TEXT,
  law_firm TEXT,
  contact_details TEXT
);

CREATE TABLE legal_issues (
  issue_id INT PRIMARY KEY,
  issue_description TEXT,
  case_id INT,
  FOREIGN KEY (case_id) REFERENCES cases (case_id)
);

CREATE TABLE precedents (
  precedent_id INT PRIMARY KEY,
  precedent_text TEXT,
  case_id INT,
  FOREIGN KEY (case_id) REFERENCES cases (case_id)
);
