tables.table_schema:
CREATE TABLE Rehabilitation_Progress (
    progress_id INT PRIMARY KEY,
    inmate_id INT REFERENCES Inmates(inmate_id),
    progress_date DATE,
    progress_description TEXT
);

CREATE TABLE Charges (
    charge_id INT PRIMARY KEY,
    inmate_id INT REFERENCES Inmates(inmate_id),
    charge_description TEXT,
    charge_date DATE
);

CREATE TABLE offenses (
    offense_id SERIAL PRIMARY KEY,
    offense_type VARCHAR(50),
    sentence_length INT,
    fine_amount DECIMAL(10,2)
);

CREATE TABLE rehabilitation_programs (
    program_id SERIAL PRIMARY KEY,
    program_name VARCHAR(100),
    description TEXT,
    duration INT
);

CREATE TABLE offenses (
    offense_id SERIAL PRIMARY KEY,
    offense_name VARCHAR(100) NOT NULL,
    severity VARCHAR(20),
    description TEXT
);

CREATE TABLE rehabilitation_programs (
    program_id SERIAL PRIMARY KEY,
    program_name VARCHAR(100) NOT NULL,
    description TEXT,
    duration INT
);

CREATE TABLE inmate_offenses (
    id SERIAL PRIMARY KEY,
    inmate_id INT,
    offense_id INT
);

CREATE TABLE inmate_programs (
    id SERIAL PRIMARY KEY,
    inmate_id INT,
    program_id INT
);
