tables.table_schema:
CREATE TABLE recidivism_data (
    id INT PRIMARY KEY,
    inmate_id INT,
    release_date DATE,
    recidivism_score FLOAT
);

CREATE TABLE IncarcerationStats (
  inmate_id INT PRIMARY KEY,
  incarceration_date DATE,
  release_date DATE,
  recidivism_status VARCHAR(20)
);

CREATE TABLE InmateActivities (
  activity_id INT PRIMARY KEY,
  inmate_id INT,
  activity_date DATE,
  activity_type VARCHAR(50)
);

CREATE TABLE reentry_programs(
    program_id SERIAL PRIMARY KEY,
    program_name VARCHAR(50) NOT NULL,
    program_type VARCHAR(50) NOT NULL,
    program_duration INT
);

CREATE TABLE inmate_history(
    history_id SERIAL PRIMARY KEY,
    inmate_id INT REFERENCES inmates(inmate _id),
    program_id INT REFERENCES reentry_programs(program_id),
    completion_status BOOLEAN
);

CREATE TABLE recidivism_reports(
    report_id SERIAL PRIMARY KEY,
    inmate_id INT REFERENCES inmates(inmate_id),
    recidivism_rate FLOAT,
    report_date DATE
);
