tables.table_schema:
CREATE TABLE Cells (
  cell_number INT PRIMARY KEY,
  capacity INT,
  prisoners_inside INT,
  prison_block VARCHAR(10)
);

CREATE TABLE PrisonStaff (
  staff_id INT PRIMARY KEY,
  name VARCHAR(50),
  position VARCHAR(50),
  prison_block VARCHAR(10)
);

CREATE TABLE Prisoners_Guards (
    guard_id INT PRIMARY KEY,
    prisoner_id INT,
    start_date DATE,
    end_date DATE
);

CREATE TABLE Cells (
    cell_id INT PRIMARY KEY,
    capacity INT,
    occupancy INT,
    location VARCHAR(50)
);
