tables.table_schema:
CREATE TABLE JusticeSystemReform (
	reform_id INT PRIMARY KEY,
	reform_type TEXT,
	reform_description TEXT,
	reform_date DATE
);

CREATE TABLE SentencingHistory (
    case_id INT PRIMARY KEY,
    sentencing_judge VARCHAR(50),
    previous_convictions INT,
    prior_sentencing_history TEXT
);

CREATE TABLE ReformAreas (
    reform_id INT PRIMARY KEY,
    reform_category VARCHAR(50),
    proposed_solution TEXT
);

CREATE TABLE sentences (
  sentence_id SERIAL PRIMARY KEY,
  crime_type VARCHAR(255),
  sentence_length INT,
  sentence_date DATE
);

CREATE TABLE judges (
  judge_id SERIAL PRIMARY KEY,
  name VARCHAR(255),
  experience_years INT
);

CREATE TABLE judges (
  judge_id INT PRIMARY KEY,
  judge_name VARCHAR(255) NOT NULL,
  court_name VARCHAR(255) NOT NULL
);

CREATE TABLE courtrooms (
  courtroom_id INT PRIMARY KEY,
  location VARCHAR(255) NOT NULL,
  presiding_judge INT,
  capacity INT
);
