tables.table_schema:
CREATE TABLE opinions (
    id INT PRIMARY KEY,
    author TEXT,
    topic TEXT,
    opinion_text TEXT
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username TEXT,
    email TEXT
);

CREATE TABLE Experts (
  expert_id INT PRIMARY KEY,
  expert_name VARCHAR(255) NOT NULL,
  expertise_area VARCHAR(255) NOT NULL
);

CREATE TABLE Commentaries (
  commentary_id INT PRIMARY KEY,
  expert_id INT,
  commentary_text TEXT NOT NULL,
  FOREIGN KEY (expert_id) REFERENCES Experts(expert_id)
);

CREATE TABLE LegalIssues (
  issue_id INT PRIMARY KEY,
  issue_description TEXT NOT NULL,
  issue_category VARCHAR(255) NOT NULL
);

CREATE TABLE experts (
    expert_id UUID PRIMARY KEY,
    name VARCHAR,
    expertise_area VARCHAR
);

CREATE TABLE issues (
    issue_id UUID PRIMARY KEY,
    issue_title VARCHAR,
    issue_description TEXT,
    issue_status VARCHAR
);
