tables.table_schema:
CREATE TABLE treaty_signatories (
    signatory_id INT PRIMARY KEY,
    treaty_id INT REFERENCES treaties(treaty_id),
    signatory_name STRING,
    signatory_country STRING
);

CREATE TABLE treaty_amendments (
    amendment_id INT PRIMARY KEY,
    treaty_id INT REFERENCES treaties(treaty_id),
    amendment_text STRING,
    amendment_date DATE
);

CREATE TABLE treaty_categories (
  category_id INT PRIMARY KEY,
  category_name VARCHAR(255),
  description TEXT
);

CREATE TABLE treaty_amendments (
  amendment_id INT PRIMARY KEY,
  treaty_id INT,
  amendment_date DATE,
  amendment_description TEXT
);

CREATE TABLE international_agreements (
    agreement_id UUID PRIMARY KEY,
    agreement_name VARCHAR(255) NOT NULL,
    country_name VARCHAR(255) NOT NULL
);

CREATE TABLE treaty_status (
    status_id UUID PRIMARY KEY,
    agreement_id UUID REFERENCES international_agreements(agreement_id),
    status VARCHAR(50) NOT NULL,
    last_updated TIMESTAMP
);

CREATE TABLE agreement_parties (
    party_id UUID PRIMARY KEY,
    agreement_id UUID REFERENCES international_agreements(agreement_id),
    party_name VARCHAR(255) NOT NULL,
    party_type VARCHAR(50)
);
