tables.table_schema:
CREATE TABLE freedom_of_speech_violations (
  violation_id INT PRIMARY KEY,
  country VARCHAR(50) NOT NULL,
  violation_type VARCHAR(50) NOT NULL,
  violation_date DATE NOT NULL
);

CREATE TABLE speech_trends (
  trend_id INT PRIMARY KEY,
  trend_date DATE NOT NULL,
  trend_description VARCHAR(255) NOT NULL,
  trend_category VARCHAR(50) NOT NULL
);

CREATE TABLE speech_violation_trends (
    id INT PRIMARY KEY,
    trend_date DATE,
    trend_type VARCHAR(50),
    trend_description TEXT
);

CREATE TABLE speech_violation_trends (
   trend_id INT PRIMARY KEY,
   violation_type VARCHAR(255),
   year INT,
   total_violations INT
);
