  case_date DATE
);

CREATE TABLE legal_arguments (
  argument_id INT PRIMARY KEY,
  argument_text TEXT,
  argument_date DATE
);

CREATE TABLE legal_arguments ( 
 argument_id UUID PRIMARY KEY, 
 argument_text TEXT, 
 case_id UUID, 
 FOREIGN KEY (case_id) REFERENCES cases (case_id) 
);

CREATE TABLE case_law ( 
 law_id UUID PRIMARY KEY, 
 law_title VARCHAR(255), 
 law_description TEXT, 
 law_date DATE 
);

CREATE TABLE case_law (
  case_id INT PRIMARY KEY,
  case_name VARCHAR,
  argument VARCHAR
);

CREATE TABLE lawyer (
  lawyer_id INT PRIMARY KEY,
  first_name VARCHAR,
  last_name VARCHAR,
  specialty VARCHAR
);

CREATE TABLE case_history (
  case_id INT,
  lawyer_id INT,
  date DATE,
  outcome VARCHAR
);

CREATE TABLE court (
  court_id INT PRIMARY KEY,
  court_name VARCHAR,
  location VARCHAR
);

CREATE TABLE law_firm (
  firm_id INT PRIMARY KEY,
  firm_name VARCHAR,
  location VARCHAR
);
