tables.table_schema:
CREATE TABLE WhistleblowerReports (
    report_id INT PRIMARY KEY,
    report_description TEXT,
    reported_by VARCHAR(50),
    report_date TIMESTAMP
);
CREATE TABLE Investigations (
    investigation_id INT PRIMARY KEY,
    report_id INT,
    investigator VARCHAR(50),
    investigation_status VARCHAR(20),
    investigation_details TEXT
);

CREATE TABLE whistleblower_reports (
    report_id UUID PRIMARY KEY,
    report_text TEXT NOT NULL,
    reporter_name VARCHAR(50) NOT NULL,
    report_date TIMESTAMP NOT NULL
);

CREATE TABLE IF NOT EXISTS regulations (
  id INT PRIMARY KEY,
  rule_number VARCHAR(50) UNIQUE,
  description TEXT,
  compliance_date DATE
);
