tables.table_schema:
CREATE TABLE employee_code_of_conduct_acknowledgments (
  employee_id INT PRIMARY KEY,
  acknowledgment_date DATE,
  acknowledgment_statement TEXT
);

CREATE TABLE employee_code_of_conduct (
    employee_id INT PRIMARY KEY,
    employee_name VARCHAR(50),
    conduct_acknowledgment_date DATE
);

CREATE TABLE EmployeeAcknowledgments (
    acknowledgment_id INT PRIMARY KEY,
    employee_id INT,
    acknowledgment_date DATE,
    compliance_status VARCHAR(10)
);

CREATE TABLE EmployeeDetails (
    employee_id INT PRIMARY KEY,
    employee_name VARCHAR(50),
    department VARCHAR(50),
    hire_date DATE
);

CREATE TABLE ComplianceOfficers (
    officer_id INT PRIMARY KEY,
    officer_name VARCHAR(50),
    department VARCHAR(50),
    email VARCHAR(50)
);
