tables.table_schema:
CREATE TABLE data_privacy_consent_agreements (
    consent_id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    agreement_date TIMESTAMP NOT NULL,
    agreement_type VARCHAR(50) NOT NULL
);

CREATE TABLE user_data_privacy (
    user_id INT PRIMARY KEY,
    has_agreed BOOLEAN NOT NULL
);

CREATE TABLE data_privacy_consents (
    consent_id INT PRIMARY KEY,
    user_id INT,
    agreement_text TEXT,
    agreement_date TIMESTAMP
);

CREATE TABLE consent_agreements (
    id INT PRIMARY KEY,
    user_id INT NOT NULL,
    agreement_date DATE NOT NULL,
    agreement_text TEXT NOT NULL,
    agreement_type VARCHAR(50) NOT NULL
);
CREATE TABLE users (
    user_id INT PRIMARY KEY,
    first_name VARCHAR(50) NOT NULL,
    last_name VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL
);
CREATE TABLE organizations (
    org_id INT PRIMARY KEY,
    org_name VARCHAR(100) NOT NULL,
    industry VARCHAR(50) NOT NULL
);
CREATE TABLE agreements_history (
    history_id INT PRIMARY KEY,
    agreement_id INT NOT NULL,
    agreement_status VARCHAR(20) NOT NULL,
    timestamp TIMESTAMP NOT NULL
);
CREATE TABLE data_categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(100) NOT NULL,
    category_description TEXT
);
