tables.table_schema:
CREATE TABLE child (
  child_id UUID NOT NULL PRIMARY KEY,
  name VARCHAR(100) NOT NULL,
  age INT NOT NULL
);

CREATE TABLE parent (
  parent_id UUID NOT NULL PRIMARY KEY,
  name VARCHAR(100) NOT NULL,
  occupation VARCHAR(100)
);

CREATE TABLE social_worker (
  worker_id UUID NOT NULL PRIMARY KEY,
  name VARCHAR(100) NOT NULL,
  specialization VARCHAR(100)
);
