tables.table_schema:
CREATE TABLE mediator_reports (
 report_id UUID PRIMARY KEY,
 report_text TEXT,
 report_date TIMESTAMP,
 report_status VARCHAR(50)
);

CREATE TABLE recommendations (
 recommendation_id UUID PRIMARY KEY,
 report_id UUID REFERENCES mediator_reports(report_id),
 recommended_actions TEXT,
 recommendation_date TIMESTAMP
);

CREATE TABLE mediator_reports (
    report_id INT PRIMARY KEY,
    date DATE,
    mediator_name VARCHAR(50),
    case_id INT
);

CREATE TABLE custody_disputes (
    case_id INT PRIMARY KEY,
    child_name VARCHAR(50),
    dispute_date DATE,
    dispute_reason TEXT
);

CREATE TABLE recommendations (
    recommendation_id INT PRIMARY KEY,
    report_id INT,
    recommended_action TEXT,
    recommended_date DATE
);

CREATE TABLE assigned_mediators (
    mediator_name VARCHAR(50) PRIMARY KEY,
    mediator_contact VARCHAR(50),
    mediator_specialization TEXT
);

CREATE TABLE clients (
  client_id INT PRIMARY KEY,
  client_name TEXT,
  contact_number TEXT,
  email TEXT
);

CREATE TABLE mediators (
  mediator_id INT PRIMARY KEY,
  mediator_name TEXT,
  mediator_type TEXT
);
