tables.table_schema:
CREATE TABLE court_cases (
  case_id INT PRIMARY KEY,
  case_name VARCHAR(255),
  case_type VARCHAR(50),
  deadline DATE
);

CREATE TABLE law_firm (
    law_firm_id UUID PRIMARY KEY,
    law_firm_name TEXT,
    address TEXT,
    phone_number TEXT
);

CREATE TABLE lawyer (
    lawyer_id UUID PRIMARY KEY,
    lawyer_name TEXT,
    specialty TEXT,
    law_firm_id UUID REFERENCES law_firm(law_firm_id)
);

CREATE TABLE court_case (
    case_id UUID PRIMARY KEY,
    case_name TEXT,
    case_type TEXT,
    lawyer_id UUID REFERENCES lawyer(lawyer_id)
);

CREATE TABLE case_schedule (
    schedule_id UUID PRIMARY KEY,
    case_id UUID REFERENCES court_case(case_id),
    schedule_date DATE,
    deadline DATE
);

CREATE TABLE case_file (
    file_id UUID PRIMARY KEY,
    case_id UUID REFERENCES court_case(case_id),
    file_name TEXT,
    file_type TEXT
);
