tables.table_schema:
CREATE TABLE regulations (
  id INT PRIMARY KEY,
  title VARCHAR(255) NOT NULL,
  description TEXT,
  enactment_date DATE
);

CREATE TABLE regulations (
    id INT PRIMARY KEY,
    regulation_name VARCHAR(255) NOT NULL,
    regulation_description TEXT,
    regulation_category VARCHAR(50)
);

CREATE TABLE statutes (
    id INT PRIMARY KEY,
    statute_name VARCHAR(255) NOT NULL,
    statute_text TEXT,
    statute_category VARCHAR(50)
);

CREATE TABLE references (
    id INT PRIMARY KEY,
    reference_name VARCHAR(255) NOT NULL,
    reference_url VARCHAR(255),
    reference_description TEXT
);

CREATE TABLE Legal_Regulations (
  regulation_id INT PRIMARY KEY,
  regulation_name TEXT,
  regulation_description TEXT
);

CREATE TABLE Statutes (
  statute_id INT PRIMARY KEY,
  statute_name TEXT,
  regulation_id INT REFERENCES Legal_Regulations(regulation_id)
);

CREATE TABLE Sections (
  section_id INT PRIMARY KEY,
  section_number TEXT,
  statute_id INT REFERENCES Statutes(statute_id)
);

CREATE TABLE Subsections (
  subsection_id INT PRIMARY KEY,
  subsection_title TEXT,
  section_id INT REFERENCES Sections(section_id)
);
