tables.table_schema:
CREATE TABLE LegalDocs (
    doc_id INT,
    doc_type TEXT,
    doc_contents TEXT,
    upload_date DATE
);

CREATE TABLE Lawyers (
    lawyer_id INT,
    lawyer_name TEXT,
    lawyer_specialty TEXT,
    lawyer_experience INT
);

CREATE TABLE CourtCases (
    case_id INT,
    case_type TEXT,
    case_description TEXT,
    case_status TEXT
);

CREATE TABLE legal_cases (
    case_id UUID PRIMARY KEY,
    case_name STRING,
    case_description TEXT,
    court_location STRING
);

CREATE TABLE lawyers (
    lawyer_id UUID PRIMARY KEY,
    lawyer_name STRING,
    practice_area STRING,
    lawyer_rating FLOAT
);

CREATE TABLE clients (
    client_id UUID PRIMARY KEY,
    client_name STRING,
    client_type STRING,
    client_location STRING
);

CREATE TABLE legal_documents (
    document_id UUID PRIMARY KEY,
    document_type STRING,
    document_content TEXT,
    case_id UUID REFERENCES legal_cases(case_id)
);
