tables.table_schema:
CREATE TABLE compliance_monitoring (
    id INT PRIMARY KEY,
    company_name VARCHAR(255),
    compliance_status BOOLEAN
);

CREATE TABLE regulatory_requirements (
    id INT PRIMARY KEY,
    requirement_name VARCHAR(255),
    description TEXT,
    deadline DATETIME
);

CREATE TABLE monitoring_team (
	supervisor_id INT NOT NULL,
	team_member_id INT NOT NULL,
	team_name VARCHAR(255) NOT NULL
);

CREATE TABLE compliance_checks (
	check_id INT NOT NULL,
	check_name VARCHAR(255) NOT NULL
);

CREATE TABLE violation_reports (
	report_id INT NOT NULL,
	violation_description TEXT NOT NULL,
	violation_date DATE NOT NULL
);

CREATE TABLE regulatory_agencies (
	agency_id INT NOT NULL,
	agency_name VARCHAR(255) NOT NULL,
	regulation_desc TEXT NOT NULL
);

CREATE TABLE regulations (
    regulation_id INT PRIMARY KEY,
    regulation_name VARCHAR(255),
    regulation_description TEXT,
    regulation_level VARCHAR(50)
);

CREATE TABLE compliance_categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(255),
    category_description TEXT
);

CREATE TABLE compliance_entities (
    entity_id INT PRIMARY KEY,
    entity_name VARCHAR(255),
    entity_type VARCHAR(50),
    entity_location VARCHAR(100)
);
