tables.table_schema:
CREATE TABLE LegalScholars (
    scholar_id UUID PRIMARY KEY,
    scholar_name VARCHAR(255) NOT NULL,
    institution VARCHAR(255) NOT NULL,
    research_interests TEXT
);

CREATE TABLE Researcher (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(50) NOT NULL,
    affiliation VARCHAR(100) NOT NULL
);

CREATE TABLE ResearchData (
    data_id INT PRIMARY KEY,
    data_description VARCHAR(200) NOT NULL,
    article_id INT,
    FOREIGN KEY (article_id) REFERENCES Article(article_id)
);
