tables.table_schema:
CREATE TABLE mediation_outcomes (
  outcome_id SERIAL PRIMARY KEY,
  session_id INT REFERENCES mediation_sessions(session_id),
  outcome TEXT,
  result BOOLEAN
);

CREATE TABLE mediation_outcomes (
  outcome_id INT PRIMARY KEY,
  session_id INT,
  outcome_text TEXT,
  resolution VARCHAR(50)
);

CREATE TABLE mediators (
  mediator_id INT PRIMARY KEY,
  mediator_name VARCHAR(50),
  specialty VARCHAR(50)
);

CREATE TABLE mediators (
    mediator_id UUID PRIMARY KEY,
    mediator_name VARCHAR(50),
    mediator_license VARCHAR(20)
);

CREATE TABLE clients (
    client_id UUID PRIMARY KEY,
    client_name VARCHAR(50),
    client_contact VARCHAR(20)
);

CREATE TABLE outcomes (
    outcome_id SERIAL PRIMARY KEY,
    session_id UUID,
    outcome_description TEXT,
    outcome_date DATE
);

CREATE TABLE feedback (
    feedback_id INT PRIMARY KEY,
    participant_id INT,
    feedback_text TEXT,
    CONSTRAINT fk_participant_id FOREIGN KEY (participant_id) REFERENCES participants(participant_id)
);
