tables.table_schema:
CREATE TABLE mediators (
  id INT PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  email VARCHAR(50) UNIQUE,
  phone_number VARCHAR(15) NOT NULL
);

CREATE TABLE Mediators (
	email VARCHAR(255) PRIMARY KEY,
	name VARCHAR(255) NOT NULL,
	phone VARCHAR(15),
	organization VARCHAR(255)
);

CREATE TABLE ContactDetails (
	contact_id SERIAL PRIMARY KEY,
	contact_type VARCHAR(20) NOT NULL,
	contact_value VARCHAR(255) NOT NULL,
	associated_entity VARCHAR(255) NOT NULL
);

CREATE TABLE Mediators (
    mediator_id INT PRIMARY KEY,
    mediator_name VARCHAR(50) NOT NULL,
    mediator_contact VARCHAR(20) NOT NULL
);

CREATE TABLE Clients (
    client_id INT PRIMARY KEY,
    client_name VARCHAR(50) NOT NULL,
    client_contact VARCHAR(20) NOT NULL
);

CREATE TABLE Stakeholders (
    stakeholder_id INT PRIMARY KEY,
    stakeholder_name VARCHAR(50) NOT NULL,
    stakeholder_contact VARCHAR(20) NOT NULL
);

CREATE TABLE Addresses (
    address_id INT PRIMARY KEY,
    address_line VARCHAR(100) NOT NULL,
    city VARCHAR(50) NOT NULL,
    state VARCHAR(2) NOT NULL,
    country VARCHAR(50) NOT NULL
);

CREATE TABLE Contacts (
    contact_id INT PRIMARY KEY,
    contact_type VARCHAR(50) NOT NULL,
    contact_detail VARCHAR(50) NOT NULL
);
