tables.table_schema:
CREATE TABLE mediators (
  id INT PRIMARY KEY,
  name VARCHAR(100) NOT NULL,
  email VARCHAR(100) UNIQUE NOT NULL
);

CREATE TABLE parties_involved (
  id INT PRIMARY KEY,
  name VARCHAR(100) NOT NULL,
  role VARCHAR(50) NOT NULL
);

CREATE TABLE logs_attachments (
  id INT PRIMARY KEY,
  log_id INT,
  attachment_path VARCHAR(200) NOT NULL,
  FOREIGN KEY (log_id) REFERENCES communication_logs(id)
);
