tables.table_schema:
CREATE TABLE MatchSchedule (
    match_id INT PRIMARY KEY,
    match_date DATE,
    team1 VARCHAR(50),
    team2 VARCHAR(50)
);

CREATE TABLE MatchSchedules (
    match_id INT PRIMARY KEY,
    match_date DATE,
    match_time TIME,
    location VARCHAR(255)
);

CREATE TABLE team (
    team_id INT PRIMARY KEY,
    team_name VARCHAR(50),
    coach_name VARCHAR(50),
    location VARCHAR(50)
);
