tables.table_schema:
CREATE TABLE seating_arrangements (
    seat_id INT PRIMARY KEY,
    section_name VARCHAR(20),
    seat_number INT,
    availability BOOLEAN
);

CREATE TABLE ticket_sales (
    ticket_id INT PRIMARY KEY,
    game_id INT,
    seat_number INT,
    price DECIMAL(10, 2)
);

CREATE TABLE seating_arrangements (
    seating_id INT PRIMARY KEY,
    game_id INT,
    section VARCHAR(50),
    seat_capacity INT
);

CREATE TABLE event_logistics (
    event_id INT PRIMARY KEY,
    game_id INT,
    venue_id INT,
    event_date DATE
);

CREATE TABLE basketball_games (
    game_id INT PRIMARY KEY,
    team1 VARCHAR(50),
    team2 VARCHAR(50),
    game_date DATE
);

CREATE TABLE tickets (
    ticket_id INT PRIMARY KEY,
    game_id INT,
    seat_number INT,
    customer_name VARCHAR(50)
);

CREATE TABLE games (
    game_id INT PRIMARY KEY,
    game_date DATE,
    arena VARCHAR(50),
    ticket_price DECIMAL(10, 2)
);

CREATE TABLE arenas (
    arena_id INT PRIMARY KEY,
    arena_name VARCHAR(50),
    location VARCHAR(100)
);

CREATE TABLE seating (
    seat_number INT PRIMARY KEY,
    arena_id INT,
    seat_type VARCHAR(20)
);

CREATE TABLE bookings (
    booking_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    game_id INT,
    ticket_id INT
);
