tables.table_schema:
CREATE TABLE tournament_brackets (
    bracket_id SERIAL PRIMARY KEY,
    bracket_name VARCHAR(255) NOT NULL,
    number_of_teams INT NOT NULL
);

CREATE TABLE championship_history (
    championship_id SERIAL PRIMARY KEY,
    team_name VARCHAR(255) NOT NULL,
    year INT NOT NULL,
    championship_type VARCHAR(255) NOT NULL
);

CREATE TABLE playoff_standings (
    playoff_id INT PRIMARY KEY,
    team_name TEXT,
    wins INT,
    losses INT
);

CREATE TABLE championship_history (
    year INT PRIMARY KEY,
    champion_team TEXT,
    runner_up_team TEXT,
    mvp_player TEXT
);

CREATE TABLE championship_history (
  history_id SERIAL PRIMARY KEY,
  league_id INTEGER NOT NULL,
  champion_team_id INTEGER NOT NULL,
  year INTEGER NOT NULL
);

CREATE TABLE tournament_brackets (
    bracket_id INT PRIMARY KEY,
    bracket_name VARCHAR(255),
    start_date DATE,
    end_date DATE
);

CREATE TABLE championship_history (
    championship_id INT PRIMARY KEY,
    team_id INT,
    championship_year INT,
    championship_title VARCHAR(255)
);

CREATE TABLE teams (
    team_id INT PRIMARY KEY,
    team_name VARCHAR(255),
    city VARCHAR(255),
    state VARCHAR(255)
);
