tables.table_schema:
CREATE TABLE teams (
    team_id INT PRIMARY KEY,
    team_name VARCHAR(50)
);

CREATE TABLE players (
    player_id INT,
    player_name VARCHAR(50),
    team_id INT,
    player_position VARCHAR(50)
);

CREATE TABLE cards (
    card_id INT,
    player_id INT,
    match_id INT,
    card_type VARCHAR(10)
);
