tables.table_schema:
CREATE TABLE league_standings (
    team_name VARCHAR(255),
    position INT,
    wins INT,
    losses INT
);

CREATE TABLE team_stats (
    team_name VARCHAR(255),
    goals_scored INT,
    games_played INT
);

CREATE TABLE league_standings (
    team_name VARCHAR(50),
    total_wins INT,
    total_losses INT,
    total_goals_scored INT
);

CREATE TABLE wins (
    team_name VARCHAR(50),
    season_year INT,
    wins INT
);

CREATE TABLE losses (
    team_name VARCHAR(50),
    season_year INT,
    losses INT
);

CREATE TABLE goals_scored (
    team_name VARCHAR(50),
    season_year INT,
    goals_scored INT
);

CREATE TABLE Teams (
    team_id INT,
    team_name VARCHAR(50),
    total_wins INT,
    total_losses INT
);

CREATE TABLE Players (
    player_id INT,
    player_name VARCHAR(50),
    team_id INT,
    position VARCHAR(50)
);
