tables.table_schema:
CREATE TABLE FanPreferences (
    fan_id INT PRIMARY KEY,
    favorite_team VARCHAR(50),
    favorite_player VARCHAR(50),
    favorite_color VARCHAR(20)
);

CREATE TABLE FanFeedback (
    feedback_id INT PRIMARY KEY,
    fan_id INT,
    timestamp TIMESTAMP,
    feedback_text TEXT
);

CREATE TABLE fan_preferences (
    fan_id INT,
    preference VARCHAR(255)
);

CREATE TABLE feedback (
    feedback_id INT,
    fan_id INT,
    feedback_text TEXT
);

CREATE TABLE loyalty_programs (
    program_id INT,
    program_name VARCHAR(255),
    benefits TEXT
);
