CREATE TABLE MatchResults (
    match_id INT PRIMARY KEY,
    winner_id INT,
    loser_id INT,
    winning_score INT,
    losing_score INT
);

CREATE TABLE PlayerStatistics (
    player_id INT PRIMARY KEY,
    matches_played INT,
    wins INT,
    losses INT
);

CREATE TABLE MatchAnalysis (
    match_id INT,
    player_id INT,
    performance_rating FLOAT
);

CREATE TABLE Players (
    player_id INT PRIMARY KEY,
    player_name VARCHAR,
    team_id INT
);

CREATE TABLE Teams (
    team_id INT PRIMARY KEY,
